export function getQuickSortAnimations(array) {

  //Setting Time complexities
  document.getElementById("Time_Worst").innerText = "O(N log N)";
  document.getElementById("Time_Average").innerText = "Θ(N log N)";
  document.getElementById("Time_Best").innerText = "Ω(N log N)";

  //Setting Space complexity
  document.getElementById("Space_Worst").innerText = "O(N)";

  const animations = [];
  if (array.length <= 1) return array;
  quickSortHelper(array, 0, array.length - 1, animations);
  return animations;
}

function quickSortHelper(
  mainArray,
  startIdx,
  endIdx,
  animations,
) {
  if (startIdx >= endIdx) return;
  const pivotIdx = partition(mainArray, startIdx, endIdx, animations);
  animations.push([pivotIdx, pivotIdx, 3]);
  quickSortHelper(mainArray, startIdx, pivotIdx - 1, animations);
  quickSortHelper(mainArray, pivotIdx + 1, endIdx, animations);
  animations([pivotIdx, pivotIdx, 4]);
}

function partition(
  mainArray,
  startIdx,
  endIdx,
  animations,
) {
  const pivot = mainArray[startIdx];
  let i = startIdx;
  let j = endIdx;

  while (i < j) {
    while (mainArray[i] <= pivot && i <= j) {
      animations.push([i, j, 0]);
      animations.push([i, j, 1]);
      animations.push([i, mainArray[i], 2]);
      i++;
    }
    while (mainArray[j] > pivot && j >= startIdx) {
      animations.push([i, j, 0]);
      animations.push([i, j, 1]);
      animations.push([j, mainArray[j], 2]);
      j--;
    }

    if (i < j) {

      animations.push([i, mainArray[j], 2]);
      animations.push([j, mainArray[i], 2]);
      swap(mainArray, i, j);
    }
  }
  animations.push([startIdx, mainArray[j], 2]);
  animations.push([j, mainArray[startIdx], 2]);
  swap(mainArray, startIdx, j);
  return j;

}

function swap(arr, i, j) {
  const temp = arr[i];
  arr[i] = arr[j];
  arr[j] = temp;
}
