import React from 'react';
import { getMergeSortAnimations } from '../sortingAlgorithms/MergeSort.js'
import { getQuickSortAnimations } from '../sortingAlgorithms/QuickSort.js'
import { getBubbleSortAnimations } from '../sortingAlgorithms/BubbleSort.js'
import './SortingVisualizer.css';




// Change this value for the speed of the animations.
var ANIMATION_SPEED_MS = 5;

// Change this value for the number of bars (value) in the array.
// const NUMBER_OF_ARRAY_BARS = 310;
var NUMBER_OF_ARRAY_BARS = 150;

// This is the main color of the array bars.
const PRIMARY_COLOR = 'turquoise';

// This is thes color of array bars that are being compared throughout the animations.
const SECONDARY_COLOR = 'red';

const THIRD_COLOR = "#90EE90"

const MID_COLOR = "green"

const AFTER_SORTING_COLOR = "turquoise"

export default class SortingVisualizer extends React.Component {
  constructor(props) {
    super(props);

    // Initialize the array
    this.state = {
      array: [],
      // NUMBER_OF_ARRAY_BARS: NUMBER_OF_ARRAY_BARS,
      // ANIMATION_SPEED_MS: ANIMATION_SPEED_MS,
    };
  }


  handleArraySizeChange = (event) => {
    const newSize = parseInt(event.target.value, 10);
    NUMBER_OF_ARRAY_BARS = newSize;
    // this.setState({ NUMBER_OF_ARRAY_BARS: newSize }, this.resetArray);
    this.setState({ arraySize: newSize }, this.resetArray);
  }

  // handleSortingSpeedChange = (event) => {
  //   const newSpeed = 21 - parseInt(event.target.value, 10);
  //   // ANIMATION_SPEED_MS = newSpeed;
  //   this.setState({ ANIMATION_SPEED_MS: newSpeed }, this.resetArray);
  //   this.setState({ ANIMATION_SPEED_MS: newSpeed });
  // }

  // componentDidMount() {
  //   this.resetArray();
  // }

  // Reset the array put all the new values
  resetArray() {
    const array = [];
    for (let i = 0; i < NUMBER_OF_ARRAY_BARS; i++) {
      // array.push(randomIntFromInterval(5, 730));
      array.push(randomIntFromInterval(5, 550));
    }
    this.setState({ array });
  }




  mergeSort() {

    // We will call getMergeSortAnimations and will sort the array and push back their i and j value 
    // how they are working in background 
    const animations = getMergeSortAnimations(this.state.array);
    // now we have all the value of i and j how they sorted accordingly

    // animation contain sequence of mergeSort so we will iterate them
    for (let i = 0; i < animations.length; i++) {

      const arrayBars = document.getElementsByClassName('array-bar');

      const [barOneIdx, barTwoIdx, count] = animations[i];

      if (count === 0) {
        const barOneStyle = arrayBars[barOneIdx].style;
        const barTwoStyle = arrayBars[barTwoIdx].style;
        const color = SECONDARY_COLOR;

        setTimeout(() => {
          barOneStyle.backgroundColor = color;
          barTwoStyle.backgroundColor = color;
        }, i * ANIMATION_SPEED_MS);
      }
      else if (count === 1) {
        const barOneStyle = arrayBars[barOneIdx].style;
        const barTwoStyle = arrayBars[barTwoIdx].style;
        const color = THIRD_COLOR;

        setTimeout(() => {
          barOneStyle.backgroundColor = color;
          barTwoStyle.backgroundColor = color;
        }, i * ANIMATION_SPEED_MS);
      }
      else if (count === 2) {
        setTimeout(() => {
          const barOneStyle = arrayBars[barOneIdx].style;
          barOneStyle.height = `${barTwoIdx}px`
        }, i * ANIMATION_SPEED_MS);
      }
      else if (count === 3) {
        const barOneStyle = arrayBars[barOneIdx].style;
        setTimeout(() => {
          barOneStyle.backgroundColor = MID_COLOR;
        }, i * ANIMATION_SPEED_MS);
      }


    }

    const arrayBars = document.getElementsByClassName('array-bar');

    setTimeout(() => {
      for (let j = 0; j < arrayBars.length; j++) {
        const barOneStyle = arrayBars[j].style;
        barOneStyle.backgroundColor = AFTER_SORTING_COLOR;
      }
    }, animations.length * ANIMATION_SPEED_MS);

  }





  quickSort() {

    const animations = getQuickSortAnimations(this.state.array);

    // animations.push([0, 0, 4]);

    for (let i = 0; i < animations.length; i++) {
      const arrayBars = document.getElementsByClassName('array-bar');
      const [barOneIdx, barTwoIdx, count] = animations[i];

      if (count === 3) {
        const barOneStyle = arrayBars[barOneIdx].style;
        setTimeout(() => {
          barOneStyle.backgroundColor = MID_COLOR;
        }, i * ANIMATION_SPEED_MS);

      } else if (count === 0 || count === 1) {
        const barOneStyle = arrayBars[barOneIdx].style;
        const barTwoStyle = arrayBars[barTwoIdx].style;
        const color = count === 0 ? SECONDARY_COLOR : THIRD_COLOR;

        setTimeout(() => {
          barOneStyle.backgroundColor = color;
          barTwoStyle.backgroundColor = color;
        }, i * ANIMATION_SPEED_MS);
      } else if (count === 2) {
        setTimeout(() => {
          const barOneStyle = arrayBars[barOneIdx].style;
          barOneStyle.height = `${barTwoIdx}px`;
        }, i * ANIMATION_SPEED_MS);
      }
    }

    const arrayBars = document.getElementsByClassName('array-bar');

    setTimeout(() => {
      for (let j = 0; j < arrayBars.length; j++) {
        setTimeout(() => {
          const barOneStyle = arrayBars[j].style;
          barOneStyle.backgroundColor = AFTER_SORTING_COLOR;
        }, animations.length * ANIMATION_SPEED_MS);

      }
    }, 0 * ANIMATION_SPEED_MS);

  }

  heapSort() {
    // We leave it as an exercise to the viewer of this code to implement this method.
  }

  bubbleSort() {

    const animations = getBubbleSortAnimations(this.state.array);

    for (let i = 0; i < animations.length; i++) {
      const arrayBars = document.getElementsByClassName('array-bar');
      const [barOneIdx, barTwoIdx, count] = animations[i];

      if (count === 3) {
        const barOneStyle = arrayBars[barOneIdx].style;
        setTimeout(() => {
          barOneStyle.backgroundColor = THIRD_COLOR;
        }, i * ANIMATION_SPEED_MS);

      } else if (count === 0 || count === 1) {
        const barOneStyle = arrayBars[barOneIdx].style;
        const barTwoStyle = arrayBars[barTwoIdx].style;
        const color = count === 0 ? SECONDARY_COLOR : PRIMARY_COLOR;

        setTimeout(() => {
          barOneStyle.backgroundColor = color;
          barTwoStyle.backgroundColor = color;
        }, i * ANIMATION_SPEED_MS);
      } else if (count === 2) {
        setTimeout(() => {
          const barOneStyle = arrayBars[barOneIdx].style;
          barOneStyle.height = `${barTwoIdx}px`;
        }, i * ANIMATION_SPEED_MS);
      }
    }

    const arrayBars = document.getElementsByClassName('array-bar');

    setTimeout(() => {
      for (let j = 0; j < arrayBars.length; j++) {
        const barOneStyle = arrayBars[j].style;
        barOneStyle.backgroundColor = AFTER_SORTING_COLOR;
      }
    }, animations.length * ANIMATION_SPEED_MS);
  }


  render() {
    const { array, arraySize } = this.state;

    return (






      <div>


        <div id='toolbar'>
          <button id='generateArray' onClick={() => this.resetArray()}>Generate New Array</button>


          <div>
            <label>Array Size:</label>
            <input
              type="range"
              min="10"
              max="200"
              value={arraySize}
              onChange={this.handleArraySizeChange}
            />
            <span>{arraySize}</span>
          </div>


          {/* <div>
            <label>Sorting Speed:</label>
            <input
              type="range"
              min="1"
              max="20"
              value={sortingSpeed}
              onChange={this.handleSortingSpeedChange}
            />
            <span>{sortingSpeed}</span>
          </div> */}



          <button id='mergesort' onClick={() => this.mergeSort()}>Merge Sort</button>
          <button id='quicksort' onClick={() => this.quickSort()}>Quick Sort</button>
          <button id='heapsort' onClick={() => this.heapSort()}>Heap Sort</button>

          <button id='bubblesort' onClick={() => this.bubbleSort()}>Bubble Sort</button>


        </div>


        <div className="array-container">
          {array.map((value, idx) => (
            <div
              className="array-bar"
              key={idx}
              style={{
                backgroundColor: PRIMARY_COLOR,
                height: `${value}px`,
              }}></div>
          ))}
        </div>

      </div>
    );
  }
}

// From https://stackoverflow.com/questions/4959975/generate-random-number-between-two-numbers-in-javascript

// Selecting Random Value Using between min and max that is pass inside the reset function 
function randomIntFromInterval(min, max) {
  // min and max included
  return Math.floor(Math.random() * (max - min + 1) + min);
}
